	/**
	 * <p>
	 * Inner class of {@link MemoryImages} to represent a single memory image with an ID and image data.
	 * </p>
	 * <p>
	 * Usage example:
	 *
	 * <pre>
	 * MemoryImage memoryImage = ...; // The image you want to show when JToggleButton is selected
	 *
	 * JToggleButton btn = new JToggleButton();
	 * btn.setName( memoryImage.getId() ); // Could be used to check if two buttons have the same image
	 * btn.setIcon( MemoryImages.getBackside() ); // Bavarian flag for UNSELECTED JToggleButton
	 * btn.setSelectedIcon( memoryImage.getImage() ); // Set the icon for SELECTED JToggleButton
	 * btn.setDisabledSelectedIcon( memoryImage.getImage() ); // Set the icon for SELECTED and DISABLED JToggleButton (keeps color)
	 * </pre>
	 * </p>
	 */
	public static class MemoryImage {

		/**
		 * ID of the pair
		 */
		private final String id;

		/**
		 * Image
		 */
		private final ImageIcon image;

		/**
		 * Create image pair
		 *
		 * @param id
		 *            ID of the pair
		 * @param image
		 *            image
		 */
		public MemoryImage( String id, ImageIcon image ) {
			super();
			this.id = id;
			this.image = image;
		}

		/**
		 * Get the ID of the pair
		 *
		 * @return ID of the pair
		 */
		public String getId() {
			return this.id;
		}

		/**
		 * Get the image
		 *
		 * @return image
		 */
		public ImageIcon getImage() {
			return this.image;
		}

	}

	/**
	 * Get all pairs
	 *
	 * @return pairs
	 */
	public static List<MemoryImage> getImages() {
		final List<MemoryImage> imgs = new ArrayList<>();
		imgs.addAll( Arrays.asList( new MemoryImage[] { MemoryImages.PAIR_FLOCKE, MemoryImages.PAIR_GENESIMMONS, MemoryImages.PAIR_BLUESBROTHERS, MemoryImages.PAIR_SHREK, MemoryImages.PAIR_HOMERSIMPSON, MemoryImages.PAIR_FOOTBALL, MemoryImages.PAIR_MONROE, MemoryImages.PAIR_GANDALF, MemoryImages.PAIR_ICEHOCKEY, MemoryImages.PAIR_JEDI, MemoryImages.PAIR_PUNK, MemoryImages.PAIR_LUITPOLD, MemoryImages.PAIR_STOIBER, MemoryImages.PAIR_WALTRAUD, MemoryImages.PAIR_ELVIS, MemoryImages.PAIR_GANDHI, MemoryImages.PAIR_STORMTROOPER } ) );
		return imgs;
	}

